def nb_char_in(char, str)
	pos = 0
	nb_occurance = 0
	loop do
		occurance = (str[pos,str.size] =~ /#{char}/)
		if( occurance != nil)
			nb_occurance += 1
			pos += occurance + 1
		end
		break if occurance == nil
	end
	return nb_occurance
end

